```ts
import got from 'got';
(async () => {
  try {
    console.log('try');
    const res = await got('https://www.baidu.com');
    console.log('got', res);
  } catch (error) {
    console.log('error', error);
  } finally {
    console.log('finally');
  }
})();
```

