	a[u++] = s;
	for (var c = 0, l = rn; c < l.length; c++) {
		var d = l[c];
		a[u++] = s + d
	}
	a[u++] = s + n;
	for (var f = 0; f < n; ++f)
		a[u++] = t();
	var v = new Uint8Array(7);
	for (f = 0; f < 7; ++f)
		v[f] = t(),
		a[u++] = v[f];
	for (f = 0; f < r.length; ++f)
		a[u++] = r[f] ^ v[f % 7];
	console.log("39",o);
	return o
